class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int count = 0;
        for(int i=0; i<m/2; i++){
            for(int j=0; j<n/2; j++){
                int sum = grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j];
                if(sum == 4 || sum == 0){
                    count += 0;
                } else if(sum == 3 || sum == 1){
                    count += 1;
                } else if(sum == 2){
                    count += 2;
                }
            }
        }

        if(m%2==1 && n%2==1) {
            if(grid[m/2][n/2] == 1){
                count++;
            }
            int flip = 0;
            int ones = 0;
            for(int j=0; j<n/2; j++){
                if(grid[m/2][j]==1){
                    ones++;
                }
                if(grid[m/2][j]!=grid[m/2][n-1-j]){
                    flip++;
                }
            }
            for(int i=0; i<m/2; i++){
                if(grid[i][n/2]==1){
                    ones++;
                }
                if(grid[i][n/2]!=grid[m-1-i][n/2]){
                    flip++;
                }
            }
            if(flip==0 && ones%2==1){
                flip += 2;
            }
            count += flip;
        } else if(m%2==1) {
            int flip = 0;
            int ones = 0;
            for(int j=0; j<n/2; j++){
                if(grid[m/2][j]==1){
                    ones++;
                }
                if(grid[m/2][j]!=grid[m/2][n-1-j]){
                    flip++;
                }
            }
            if(flip==0 && ones%2==1){
                flip += 2;
            }
            count += flip;
        } else if(n%2==1) {
            int flip = 0;
            int ones = 0;
            for(int i=0; i<m/2; i++){
                if(grid[i][n/2]==1){
                    ones++;
                }
                if(grid[i][n/2]!=grid[m-1-i][n/2]){
                    flip++;
                }
            }
            if(flip==0 && ones%2==1){
                flip += 2;
            }
            count += flip;
        }

        return count;
    }
};
