class Solution {
public:
    int minFlips(vector<vector<int>>& matrix) {
        int rows = matrix.size(), cols = matrix[0].size();
        int flipCount = 0;

        // Check center element if both rows and cols are odd
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (matrix[rows / 2][cols / 2] == 1) {
                flipCount += 1;
            }
        }

        vector<int> shifts(3, 0);
        // Check the middle row if rows are odd
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
                shifts[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        // Check the middle column if cols are odd
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
                shifts[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        // Evaluate the counts in shifts array
        if (shifts[2] % 2 == 1) {
            if (shifts[1] > 0) {
                flipCount += shifts[1];
            } else {
                flipCount += 2;
            }
        } else {
            flipCount += shifts[1];
        }

        // Process the rest of the grid
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int a = matrix[i][j];
                int b = matrix[rows - 1 - i][j];
                int c = matrix[i][cols - 1 - j];
                int d = matrix[rows - 1 - i][cols - 1 - j];

                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) {
                    flipCount += min(sum, 4 - sum);
                }
            }
        }

        return flipCount;
    }
};
