class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> arr(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int s = grid[n / 2][a] + grid[n / 2][b];
                arr[s] ++;
                a++;
                b--;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int s = grid[a][m / 2] + grid[b][m / 2];
                arr[s] ++;
                a ++;
                b --;
            }
        }

        if (arr[2] % 2 == 1) {
            if (arr[1] > 0) {
                ans += arr[1];
            } else {
                ans += 2;
            }
        } else {
            ans += arr[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sm = a + b + c + d;

                if (sm != 0 && sm != 4) {
                    ans += min(sm, 4 - sm);
                }
            }
        }

        return ans;
    }
};
