class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
     vector<vector<int>>d(grid.size(),vector<int>(grid[0].size(),0));
        int ans=0,s2=0,s1=0,s0=0;
        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[0].size();j++)
            {
                if(d[i][j]==0)
                {
                    int l=grid.size()-1-i;
                    int m=grid[0].size()-1-j;
                    if(i==l&&j==m)
                    {
                      if(grid[i][j]==1)  
                      {
                          ans++;
                          d[i][j]=1;
                      }
                    }
                    else if(i==l)
                    {

                        int sm=grid[i][j]+grid[i][m];
                        if(sm==2)
                        {
                            s2++;
                        }
                        else if(sm==1)
                            s1++;
                        else
                            s0++;
                         d[i][j]=1;
                           d[i][m]=1;
                    }
                    else if(j==m)
                    {
                       int sm=grid[i][j]+grid[l][j];
                        if(sm==2)
                        {
                            s2++;
                        }
                        else if(sm==1)
                            s1++;
                        else
                            s0++;
                         d[i][j]=1;
                           d[l][j]=1;
                    }
                    else
                    {
                       int tt=grid[i][j]+grid[l][j]+grid[i][m]+grid[l][m] ;
                        if(tt==3||tt==1)
                            ans++;
                        else if(tt==2)
                            ans+=2;
                             d[i][j]=1;
                           d[l][j]=1;
                        d[i][m]=1;
                        d[l][m]=1;
                    }
                }
            }
        }
       ans+=s1;
        if(s2%2!=0)
        {
            if(s1==0)
                ans+=2;
        }
        return ans;
    }
};
