class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int res = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                res += 1;
            }
        }

        vector<int> shift(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sum = grid[n / 2][a] + grid[n / 2][b];
                shift[sum] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int sum = grid[a][m / 2] + grid[b][m / 2];
                shift[sum] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) res += shift[1];
            else res += 2;
        } 
        else res += shift[1];


        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sum = a + b + c + d;

                if(sum != 0 && sum != 4) {
                    res += min(sum, 4 - sum);
                }
            }
        }

        return res;
    }
};
