class Solution:
    def minFlips(self, grid):
        n = len(grid)
        m = len(grid[0])
        ans = 0

        if n % 2 == 1 and m % 2 == 1:
            if grid[n // 2][m // 2] == 1:
                ans += 1

        shift = [0] * 3

        if n % 2 == 1:
            for a in range(m // 2):
                sm = grid[n // 2][a] + grid[n // 2][m - 1 - a]
                shift[sm] += 1

        if m % 2 == 1:
            for a in range(n // 2):
                sm = grid[a][m // 2] + grid[n - 1 - a][m // 2]
                shift[sm] += 1

        ans += shift[1] if shift[2] % 2 == 0 else (shift[1] if shift[1] > 0 else 2)

        for i in range(n // 2):
            for j in range(m // 2):
                sm = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j]
                if sm != 0 and sm != 4:
                    ans += min(sm, 4 - sm)

        return ans
