class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size();
        int m=grid[0].size();

        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt=0;
                if(grid[i][j]==1){
                    cnt++;
                }
                if(grid[i][m-j-1]==1){
                    cnt++;
                }
                if(grid[n-i-1][j]==1){
                    cnt++;
                }
                if(grid[n-i-1][m-j-1]==1){
                    cnt++;
                }
                ans+=min(cnt,4-cnt);
            }
        }
        int num2=0;
        int check=0;
        if(n%2==1){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]==1 && grid[n/2][m-i-1]==1){
                    num2+=2;
                }
                else if(grid[n/2][i]+grid[n/2][m-i-1]==1){
                    ans++;
                    check=1;
                }
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]==1 && grid[n-i-1][m/2]==1){
                    num2+=2;
                }
                else if(grid[i][m/2]+grid[n-i-1][m/2]==1){
                    ans++;
                    check=1;
                }
            }
        }

        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1){
                ans++;
            }
        }
        if(num2%4!=0){
            if(!check){
                ans+=2;
            }
        }
        return ans;
    }
};
