class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i = 0; i<n/2; i++){
            for(int j = 0; j<m/2; j++){
                int jnew = m-1-j, inew = n-1-i;
                int ones = 0;
                vector<int> is = {i, inew};
                vector<int> js = {j, jnew};
                for(int k = 0; k<2; k++){
                    for(int l = 0; l<2; l++){
                        if(grid[is[k]][js[l]])ones++;
                    }
                }
                ans += min(ones, 4-ones);
            }
        }
        // cout<<ans<<endl;
        int cnt = 0, ones = 0;
        if(n%2){
            int mid = n/2;
            for(int j = 0; j<m/2; j++){
                if(grid[mid][j]!=grid[mid][m-1-j])cnt++;
                else if(grid[mid][j]==grid[mid][m-1-j] && grid[mid][m-1-j]==1)ones += 2;
            }
        }
        if(m%2){
            int mid = m/2;
            for(int i = 0; i<n/2; i++){
                if(grid[i][mid]!=grid[n-1-i][mid])cnt++;
                else if(grid[i][mid]==grid[n-1-i][mid] && grid[n-1-i][mid]==1)ones+= 2;
            }
        }
        // cout<<cnt<<endl;
        ones%=4;
        if(ones == 2){
            if(cnt>=1){
                ans++; cnt--;
                ones = 0;
            }
        }
        // if(ones<= cnt)ones = 0;
        ans+= ones;
        // cout<<ones<<endl;
        ans+=cnt;
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1)ans++;

        return ans;
    }
};
