class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
       int n=grid.size(), m=grid[0].size();
    int ans=0;
    if(n%2==1 && m%2==1){
        ans+=grid[n/2][m/2];
        int pairs=0;
        int cnt=0;
        for(int i=0;i<n/2;i++){
            if(grid[i][m/2]==1 && grid[n-i-1][m/2]==1) pairs++;
            cnt+=grid[i][m/2]; cnt+=grid[n-i-1][m/2];
        }
        for(int j=0;j<m/2;j++){
            if(grid[n/2][j]==1 && grid[n/2][m-j-1]==1) pairs++;
            cnt+=grid[n/2][j]; cnt+=grid[n/2][m-j-1];
        }
        cnt-=(2*pairs);
        if(pairs%2==1){
            if(cnt<=0) cnt+=2;
        }
        ans+=cnt;
    }
    else if(n%2==1){
        int pairs=0;
        int cnt=0;
        for(int j=0;j<m/2;j++){
            if(grid[n/2][j]==1 && grid[n/2][m-j-1]==1) pairs++;
            cnt+=grid[n/2][j]; cnt+=grid[n/2][m-j-1];
        }
        cnt-=(2*pairs);
        if(pairs%2==1){
            if(cnt<=0) cnt+=2;
        }
        ans+=cnt;
    }
    else if(m%2==1){
        int pairs=0;
        int cnt=0;
        for(int i=0;i<n/2;i++){
            if(grid[i][m/2]==1 && grid[n-i-1][m/2]==1) pairs++;
            cnt+=grid[i][m/2]; cnt+=grid[n-i-1][m/2];
        }
        cnt-=(2*pairs);
        if(pairs%2==1){
            if(cnt<=0) cnt+=2;
        }
        ans+=cnt;
    }
    for(int i=0;i<n/2;i++){
        for(int j=0;j<m/2;j++){
            int cnt=0;
            cnt+=grid[i][j];
            cnt+=grid[n-i-1][j];
            cnt+=grid[i][m-j-1];
            cnt+=grid[n-i-1][m-j-1];
            ans+=min(cnt,4-cnt);
        }
    }
    return ans;
    }
};
