class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
          int n = grid.size(), m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> res(3, 0);
        if (n % 2 == 1) {
            int p = 0, q = m - 1;
            while (p < q) {
                int s = grid[n / 2][p] + grid[n / 2][q];
                res[s] += 1;
                p += 1;
                q -= 1;
            }
        }

        if (m % 2 == 1) {
            int p = 0, q = n - 1;
            while (p < q) {
                int s = grid[p][m / 2] + grid[q][m / 2];
                res[s] += 1;
                p += 1;
                q -= 1;
            }
        }

        if (res[2] % 2 == 1) {
            if (res[1] > 0) {
                ans += res[1];
            } else {
                ans += 2;
            }
        } else {
            ans += res[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int x = grid[i][j];
                int y = grid[n - 1 - i][j];
                int z = grid[i][m - 1 - j];
                int w = grid[n - 1 - i][m - 1 - j];

                int sm = w + z + y + x;

                if (sm != 0 && sm != 4) {
                    ans += min(sm, 4 - sm);
                }
            }
        }

        return ans;
    }
};
