class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int ans=0;
        int m=a.size(),n=a[0].size();
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int x=a[i][j]+a[i][n-j-1]+a[m-i-1][j]+a[m-i-1][n-j-1];
                ans+=min(x,4-x);
            }
        }
        if(m%2==0 && n%2==0)
            return ans;
        if(m%2!=0 && n%2!=0){
            if(a[m/2][n/2]==1)
                ans++;
        }
        int c=0,flag=0;
        if(m%2!=0){
            for(int j=0;j<n/2;j++)
                if(a[m/2][j]!=a[m/2][n-j-1]){
                    flag=1;
                    ans++;
                }
                else if(a[m/2][j]==1)
                    c+=2;
        }
        if(n%2!=0){
            for(int i=0;i<m/2;i++)
                if(a[i][n/2]!=a[m-i-1][n/2]){
                    flag=1;
                    ans++;
                }
                else if(a[i][n/2]==1)
                    c+=2;
        }
        if(c%4==0 || flag==1)
            return ans;
        return ans+2;
    }
};
