class Solution {
    public int minFlips(int[][] grid) {
        int ans=0;
        int count0=0;
         int count1=0;
        for(int i=0;i<grid.length/2;i++)
        for(int j=0;j<grid[0].length/2;j++){

            if(grid[i][j]==0)
            count0++;
            else
            count1++;
            if(grid[grid.length-1-i][j]==0)
            count0++;
            else
            count1++;
            if(grid[i][grid[0].length-1-j]==0)
            count0++;
            else
            count1++;
            if(grid[grid.length-1-i][grid[0].length-1-j]==0)
            count0++;
            else
            count1++;
            ans+=Math.min(count0,count1);

            count0=0;
            count1=0;
        }
        int count=0;
        if(grid.length%2==1)
        {
            count1=0;
            for(int i=0;i<grid[0].length/2;i++)
            if(grid[grid.length/2][i]!=grid[grid.length/2][grid[0].length-1-i])
            {
                count++;
            }
            else if(grid[grid.length/2][i]==1)
            count1+=2;


        }
        if(grid[0].length%2==1)
        {

            for(int i=0;i<grid.length/2;i++)
            if(grid[i][grid[0].length/2]!=grid[grid.length-1-i][grid[0].length/2])
            {
                count++;
            }
            else if(grid[i][grid[0].length/2]==1)
            count1+=2;


        }
        if(count1%4!=0&&count==0)
        ans+=2;
        ans+=count;
        if(grid[0].length%2==1&&grid.length%2==1&&grid[grid.length/2][grid[0].length/2]==1)
        ans++;
        return ans;
    }
}
