class Solution {
public:
    ll dp[200500][6];
    ll solve(int i, int p, vector<pll>& v) {
        if (i == v.size()) {
            if (p == 0) return 0;
            return 1e10;
        }

        if (dp[i][p] != -1) return dp[i][p];

        ll ans = 1e10;
        ans = min(ans, solve(i + 1, p, v) + v[i].F);
        ll d = v[i].S - v[i].F;
        ans = min(ans, solve(i + 1, (p + v[i].S) % 4, v) + d);

        return dp[i][p] = ans;
    }

    int minFlips(vector<vector<int>>& v) {
        vector<pll> t;
        ll n = v.size(), m = v[0].size();

        int i = 0, j = n - 1;
        while (i <= j) {
            ll x = 0, y = m - 1;
            while (x <= y) {
                ll sum = 0;
                ll b = 0;
                set<pll> st;

                st.in({i, x});
                st.in({i, y});
                st.in({j, x});
                st.in({j, y});
                b = st.size();

                for (auto &it : st) {
                    sum += v[it.F][it.S];
                }

                t.pb({sum, b});
                x++;
                y--;
            }
            i++;
            j--;
        }

        memset(dp, -1, sizeof(dp));
        return solve(0, 0, t);
    }
};
