#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int res = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                res += 1;
            }
        }

        vector<int> shift(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int s = grid[n / 2][a] + grid[n / 2][b];
                shift[s] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int s = grid[a][m / 2] + grid[b][m / 2];
                shift[s] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                res += shift[1];
            } else {
                res += 2;
            }
        } else {
            res += shift[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a1 = grid[i][j];
                int a2 = grid[n - 1 - i][j];
                int a3 = grid[i][m - 1 - j];
                int a4 = grid[n - 1 - i][m - 1 - j];

                int s = a1 + a2 +a3 + a4;

                if (s != 0 && s != 4) {
                    res += min(s, 4 - s);
                }
            }
        }

        return res;
    }
};
