class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size(), cols = grid[0].size();
        int flipCount = 0;

        // Handle the center element if both rows and cols are odd
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                flipCount++;
            }
        }

        vector<int> rowColFlips(3, 0);

        // Handle middle row if rows are odd
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                rowColFlips[sum]++;
                left++;
                right--;
            }
        }

        // Handle middle column if cols are odd
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                rowColFlips[sum]++;
                top++;
                bottom--;
            }
        }

        // Adjust flip count based on row and column flip sums
        if (rowColFlips[2] % 2 == 1) {
            if (rowColFlips[1] > 0) {
                flipCount += rowColFlips[1];
            } else {
                flipCount += 2;
            }
        } else {
            flipCount += rowColFlips[1];
        }

        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    flipCount += min(sum, 4 - sum);
                }
            }
        }

        return flipCount;
    }
};
