func minFlips(grid [][]int) int {
	n, m := len(grid), len(grid[0])
	res := 0
	for i := 0; i < n/2; i++ {
		for j := 0; j < m/2; j++ {
			cnt1 := 0
			cnt0 := 0
			if grid[i][j] == 1 {
				cnt1++
			} else {
				cnt0++
			}

			if grid[n-1-i][j] == 1 {
				cnt1++
			} else {
				cnt0++
			}

			if grid[i][m-1-j] == 1 {
				cnt1++
			} else {
				cnt0++
			}

			if grid[n-1-i][m-1-j] == 1 {
				cnt1++
			} else {
				cnt0++
			}

			res += minInt(cnt0, cnt1)
		}
	}

	gc1 := 0
	gc0 := 0
	diff := 0
	if m%2 == 1 {
		for i := 0; i < n/2; i++ {
			if grid[i][m/2]%2 == 0 {
				gc0++
			} else {
				gc1++
			}
			if grid[n-1-i][m/2]%2 == 0 {
				gc0++
			} else {
				gc1++
			}
			if grid[i][m/2] != grid[n-1-i][m/2] {
				diff++
			}
		}
	}
	if n%2 == 1 {
		for j := 0; j < m/2; j++ {
			if grid[n/2][j]%2 == 0 {
				gc0++
			} else {
				gc1++
			}
			if grid[n/2][m-j-1]%2 == 0 {
				gc0++
			} else {
				gc1++
			}
			if grid[n/2][j] != grid[n/2][m-j-1] {
				diff++
			}
		}
	}
	if n%2 == 1 && m%2 == 1 {
		if grid[n/2][m/2]%2 == 1 {
			res++
		}
	}

	res += diff
	if gc1+diff < 4 {
		res += gc1 - diff
	} else {
		if gc1%4 == 2 && diff == 0 {
			res += 2
		}
	}

	return res

}

func minInt(a, b int) int {
	if a < b {
		return a
	}
	return b
}
