class Solution {
public:
    int minFlips(vector<vector<int>>& nums) {
        int row = nums.size(), col = nums[0].size();
        int x =0, y =0, res=0;
        for(int i=0;i<(row+1)/2;i++){
            for(int j=0;j<(col+1)/2;j++){
                map<pair<int,int>,int> m;
                m[{i,j}]++; m[{i,col-1-j}]++; m[{row-1-i,j}]++; m[{row-1-i,col-1-j}]++;
                int a=0, b=0;
                for(auto it:m){
                    if(nums[it.first.first][it.first.second]==0) a++;
                    else b++;
                }
                if(m.size()==4) res += min(a,b);
                else if(m.size()==2){
                    if(a==0) x++;
                    else{
                        if(a==1 && b==1){
                            res++; y++;
                        }
                    }
                }
                else if(m.size()==0 || m.size()==1 || m.size()==3){
                    res += b;
                }
            }
        }
        if(x%2!=0){
            if(y>0) return res;
            else{
                res = res + 2;
                return res;
            }
        }
        else return res;
        throw;
    }
};
