class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int sol = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                sol += 1;
            }
        }

        vector<int> move(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sum = grid[n / 2][a] + grid[n / 2][b];
                move[sum] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int sum = grid[a][m / 2] + grid[b][m / 2];
                move[sum] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (move[2] % 2 == 1) {
            if (move[1] > 0) {
                sol += move[1];
            } else {
                sol += 2;
            }
        } else {
            sol += move[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) {
                    sol += min(sum, 4 - sum);
                }
            }
        }

        return sol;
    }
};
