class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        @cache
        def dfs(i,j,md):
            r1 = i
            r2 = len(grid)-i-1
            c1 = j
            c2 = len(grid[0])-j-1
            if r1>r2:
                if md==0:
                    return 0
                return inf
            if c1>c2:
                return dfs(i+1,0,md)
            if r1==r2 and c1==c2:
                if md==0 and grid[r1][c1]==0:
                    return 0
                elif md==0 and grid[r1][c1]==1:
                    return 1
                if md==1 and grid[r1][c1]==1:
                    return 1
                if md==3 and grid[r1][c1]==0:
                    return 1
                return inf
            if c1==c2:
                zc = 0
                oc = 0
                if grid[r1][c1]==0:
                    zc+=1
                else:
                    oc+=1
                if grid[r2][c2]==0:
                    zc+=1
                else:
                    oc+=1
                return min(oc+dfs(i,j+1,md),zc+dfs(i,j+1,(md+2)%4))
            if r1==r2:
                zc=0
                oc = 0
                if grid[r1][c1]==0:
                    zc+=1
                else:
                    oc+=1
                if grid[r1][c2]==0:
                    zc+=1
                else:
                    oc+=1
                return min(oc+dfs(i,j+1,md),zc+dfs(i,j+1,(md+2)%4))
            zc = 0
            oc = 0
            if grid[r1][c1]==0:
                zc+=1
            else:
                oc+=1
            if grid[r1][c2]==0:
                zc+=1
            else:
                oc+=1
            if grid[r2][c1]==0:
                zc+=1
            else:
                oc+=1
            if grid[r2][c2]==0:
                zc+=1
            else:
                oc+=1
            res = min(oc+dfs(i,j+1,md),zc+dfs(i,j+1,(md+4)%4))
            #print(i,j,md,oc,zc,res)
            return res
        return dfs(0,0,0)
