from typing import List


class Solution:
    def minFlips(self, matrix: List[List[int]]) -> int:
        rows = len(matrix)
        cols = len(matrix[0])

        flip_count = 0

        # Traverse through the 2x2 sub-matrices
        for x in range(rows // 2):
            for y in range(cols // 2):
                cell_group = [
                    matrix[x][y],
                    matrix[rows - x - 1][y],
                    matrix[x][cols - y - 1],
                    matrix[rows - x - 1][cols - y - 1]
                ]

                ones_count = sum(cell_group)

                flip_count += min(ones_count, 4 - ones_count)

        additional_flips = 0
        middle_ones = 0

        # Process the middle row if the number of rows is odd
        if rows % 2 != 0:
            for y in range(cols // 2):
                if matrix[rows // 2][y] != matrix[rows // 2][cols - y - 1]:
                    flip_count += 1
                    additional_flips += 1
                else:
                    if matrix[rows // 2][y] == 1:
                        middle_ones += 1

        # Process the middle column if the number of columns is odd
        if cols % 2 != 0:
            for x in range(rows // 2):
                if matrix[x][cols // 2] != matrix[rows - x - 1][cols // 2]:
                    flip_count += 1
                    additional_flips += 1
                else:
                    if matrix[x][cols // 2] == 1:
                        middle_ones += 1

        # Handle the central element if both rows and columns are odd
        if rows % 2 != 0 and cols % 2 != 0 and matrix[rows // 2][cols // 2] == 1:
            flip_count += 1

        if additional_flips == 0 and middle_ones % 2 != 0:
            flip_count += 2

        return flip_count
