class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int ans = 0;

        for(int i=0; i<n/2; i++) {
            for(int j=0; j<m/2; j++) {
                int ones = 0;
                int zeroes = 0;

                if(grid[i][j]) ones++;
                else zeroes++;

                if(grid[i][m-j-1]) ones++;
                else zeroes++;

                if(grid[n-i-1][j]) ones++;
                else zeroes++;

                if(grid[n-i-1][m-j-1]) ones++;
                else zeroes++;

                ans += min(ones, zeroes);
            }
        }
        cout<<ans<<endl;

        if(n%2 == 1 && m%2==0) {
            int ones = 0;
            int diffs = 0;
