                int a = grid[n/2][i], b = grid[n/2][m-i-1];

                if(a==b) {
                    if(a==1) ones+=2;
                } else {
                    diffs++;
                }
            }

            if(ones%4!=0 && diffs==0) {
                ans+=2;
            }
            else {
                ans += diffs;
            }
        }
        cout<<ans<<endl;
