class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int i, j, m = grid.size(), n = grid[0].size(), ans = 0, cnt, flag = 0;
        for (i = m >> 1; i--;) {
            for (j = n >> 1; j--;) {
                cnt = grid[i][n - j - 1] + grid[m - i - 1][n - j - 1];
                cnt += grid[i][j] + grid[m - i - 1][j];
                ans += min(cnt, 4 - cnt);
            }
        }
        cnt = 0;
        if (m & 1) {
            for (j = n >> 1; j--;) {
                if (grid[m >> 1][j] && grid[m >> 1][n - j - 1]) cnt++;
                if (grid[m >> 1][j] != grid[m >> 1][n - j - 1]) ans++, flag = 1;
            }
        }
        if (n & 1) {
            for (i = m >> 1; i--;) {
                if (grid[i][n >> 1] && grid[m - i - 1][n >> 1]) cnt++;
                if (grid[i][n >> 1] != grid[m - i - 1][n >> 1]) ans++, flag = 1;
            }
        }
        if ((cnt & 1) && !flag) ans += 2;
        return ans + ((m & 1) && (n & 1) && grid[m >> 1][n >> 1]);
    }
};
