class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(), m=grid[0].size();
        int ans=0;
        if(n==1) {
            int op=0, zp=0, up=0;
            for(int j=0;j<m/2;j++) {
                op += (grid[0][j]==1 && grid[0][m-j-1]==1);
                up += (grid[0][j]!=grid[0][m-j-1]);
            }
            zp = m/2 - op - up;
            if(op%2) {
                if(up>0) {
                    ans += 1;
                    up--;
                }
                else
                    ans += 2;
            }
            ans += up;
            if(m%2)
                ans += (grid[0][m/2]==1);
        } else if(m==1) {
            int op=0, zp=0, up=0;
            for(int i=0;i<n/2;i++) {
                op += (grid[i][0]==1 && grid[n-i-1][0]==1);
                up += (grid[i][0]!=grid[n-i-1][0]);
            }
            zp = n/2 - op - up;
            // cout<<n/2<<"-"<<op<<"-"<<up<<endl;
            if(op%2) {
                if(up>0) {
                    ans += 1;
                    up--;
                }
                else
                    ans += 2;
            }
            ans += up;
            if(n%2)
                ans += (grid[n/2][0]==1);
            // cout<<op<<" "<<zp<<" "<<up<<endl;
        } else {
            for(int i=0;i<n/2;i++)
                for(int j=0;j<m/2;j++) {
                    int val = grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                    ans += min(val, 4-val);
                }
            int up=0, op=0; // unequal pair, one pair
            if(m%2) {
                for(int i=0;i<n/2;i++) {
                    up += (grid[i][m/2]!=grid[n-i-1][m/2]);
                    op += (grid[i][m/2]==1 && grid[n-i-1][m/2]==1);
                }
            }
            if(n%2) {
                for(int j=0;j<m/2;j++) {
                    up += (grid[n/2][j]!=grid[n/2][m-j-1]);
                    op += (grid[n/2][j]==1 && grid[n/2][m-j-1]==1);
                }
            }
            if(op%2) {
                if(up>0) {
                    ans += 1;
                    up--;
                }
                else
                    ans += 2;
            }
            ans += up;
            if(m%2 && n%2)
                ans += (grid[n/2][m/2]==1);
        }
        return ans;
    }
};
