class Solution {
public:
    int centerFlip(vector<vector<int>>& grid, int rows, int cols) {
        if (rows % 2 == 1 && cols % 2 == 1) {
            return grid[rows / 2][cols / 2] == 1 ? 1 : 0;
        }
        return 0;
    }

    int middleRowFlips(vector<vector<int>>& grid, int rows, int cols, vector<int>& shifts) {
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                shifts[sum]++;
                left++;
                right--;
            }
        }
        return 0;
    }

    int middleColumnFlips(vector<vector<int>>& grid, int rows, int cols, vector<int>& shifts) {
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                shifts[sum]++;
                top++;
                bottom--;
            }
        }
        return 0;
    }

    int subgridFlips(vector<vector<int>>& grid, int rows, int cols) {
        int flips = 0;
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int a = grid[i][j];
                int b = grid[rows - 1 - i][j];
                int c = grid[i][cols - 1 - j];
                int d = grid[rows - 1 - i][cols - 1 - j];
                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) {
                    flips += min(sum, 4 - sum);
                }
            }
        }
        return flips;
    }

    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int totalFlips = 0;
        vector<int> shifts(3, 0);

        totalFlips += centerFlip(grid, rows, cols);
        middleRowFlips(grid, rows, cols, shifts);
        middleColumnFlips(grid, rows, cols, shifts);

        if (shifts[2] % 2 == 1) {
            if (shifts[1] > 0) {
                totalFlips += shifts[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += shifts[1];
        }

        totalFlips += subgridFlips(grid, rows, cols);

        return totalFlips;
    }
};
