class Solution {
public:
    #define nl '\n'
#define ld long double
#define endl '\n'
#define vi vector<int>
#define pii pair<int, int>
#define fr first
#define sc second
#define sp " "
#define mkpr make_pair
#define sz(x) x.size()
#define all(x) (x).begin(), (x).end()
#define gcd(a, b) __gcd(a, b)
#define vpi vector<pii>
#define vvi vector<vi>
#define vvpi vector<vpi>
    int minFlips(vector<vector<int>>& g) 
    {
        int n= g.size();
        int m= g[0].size();
       map<int,int>cnt;  int op=0;
        for(int i=0;i<n;i+=1)
        {
            for(int j=0;j<m;j+=1)
            {
                // lets check all 4 and update
                 map<int,int> mp;
                set< pii> temp;
                temp.insert({i,j});
                temp.insert({i,m-1-j});
                temp.insert({n-i-1,j});
                temp.insert({n-i-1,m-1-j});
                for(auto p: temp)
                {
                    int x= p.fr; int y= p.sc;
                    mp[g[x][y]] ++;
                }
                // mp[ g[i][j] ]++;
                // mp[ g[i][m-1-j] ]++;
                // mp[ g[n-i-1][j] ]++;
                // mp[ g[n-i-1][m-1-j] ] ++;
                mp[0]++; mp[0]--; mp[1]++; mp[1]--;
                int ele ; 
                if(mp[0]< mp[1])
                    ele=1;
                else
                 if(mp[0]>mp[1])
                    ele=0 ;
                else
                if(mp[0]==mp[1])
                {
                    ele=0; 
                    cnt[2*mp[0]]++;
                }

                op += min(mp[0], mp[1]);
                g[i][j]=ele;g[i][m-1-j] =ele;g[n-i-1][j] =ele;g[n-i-1][m-1-j]=ele;
            }
        }
        int c1=0;
        for(int i=0;i<n;i+=1)
        {
            for(int j=0;j<m;j+=1)
            {
                if(g[i][j])
                    c1++;
                // cout<<g[i][j]<<" ";
            }
            // cout<<endl;
        }

        int md= c1%4 ;
        if( md>=2 && cnt[2])
            md-=2;
         md -= min( md, cnt[1]);

        // cout<<op<<" "<<ex<<endl;
        // cout<<
       return op+md;
    }
};
