int minFlips(int** grid, int gridSize, int* gridColSize) 
{
    int i=0;
    int j=0;
    int num_part=0;
    int nums=0;
    for(i=0; i<gridSize/2; i++)
        for(j=0; j<(*gridColSize)/2; j++)
    {
        if((i!=(gridSize-1-i))&&(j!=((*gridColSize)-1-j)))
        { num_part=grid[i][j] + grid[gridSize-1-i][j] + grid[i][*gridColSize-1-j] + grid[gridSize-1-i][*gridColSize-1-j];
          nums += (num_part>=2)? (4-num_part) : num_part;}
    }

    if(((gridSize%2)!=0) && (((*gridColSize)%2!=0)))
        nums+=(grid[gridSize/2][(*gridColSize)/2]==1);

    int one_pairs=0;
    int diff_pairs=0;
    if((gridSize%2)!=0)
    {   
        for(j=0; j<(*gridColSize)/2; j++)
        if(j!=(*gridColSize-1-j))
            {
                if(grid[gridSize/2][j]!=grid[gridSize/2][*gridColSize-1-j])
                    diff_pairs++;
                else if((grid[gridSize/2][j]==1)&&(grid[gridSize/2][*gridColSize-1-j]==1))
                    one_pairs++;
            }       
    }

     if(((*gridColSize)%2)!=0)
    {
        for(i=0; i<gridSize/2; i++)
        if(i!=(gridSize-1-i))
            {
                if(grid[i][(*gridColSize)/2]!=grid[gridSize-1-i][(*gridColSize)/2])
                    diff_pairs++;
                else if((grid[i][(*gridColSize)/2]==1)&&(grid[gridSize-1-i][(*gridColSize)/2]==1))
                    one_pairs++;
            }      
    }

    if(one_pairs%2==0)
        nums += diff_pairs;
    else if(diff_pairs==0)
        nums += 2;
    else
        nums += diff_pairs;    

    return nums;
}
