class Solution {
public:
    int minFlips(vector<vector<int>>& a) {

         int n=a.size();
        int m=a[0].size();
        int cnt=0,cnt2=0;
        int f=0;
        if(n%2){
            for(int i=0;i<m;i++){
                if(a[n/2][i]!=a[n/2][m-1-i])f=1;
            }
        }
         if(m%2){
            for(int i=0;i<n;i++){
                if(a[i][m/2]!=a[n-1-i][m/2])f=1;
            }
        }

        for(int i=0;i<n;i++){

            for(int j=0;j<m/2;j++){
                if(a[i][j]==a[i][m-1-j] && a[i][j]==a[n-1-i][j])continue;
                cnt++;
            if(a[i][j]!=a[i][m-1-j] && a[i][j]!=a[n-1-i][j])a[i][j]=a[i][m-1-j];
                else if(a[i][j]!=a[i][m-1-j] && a[i][j]==a[n-1-i][j])a[i][m-1-j]=a[i][j];
                else
                a[n-1-i][j]=a[i][j];
            }


        }
         for(int i=0;i<m;i++){

            for(int j=0;j<n/2;j++){
                 if(a[j][i]==a[j][m-1-i] && a[j][i]==a[n-1-j][i])continue;
                cnt++;
                if(a[j][i]!=a[j][m-1-i] && a[j][i]!=a[n-1-j][i])a[j][i]=a[j][m-1-i];
                else if(a[j][i]!=a[j][m-1-i] && a[j][i]==a[n-1-j][i])a[j][m-1-i]=a[j][i];
                else
                a[n-1-j][i]=a[j][i];

            }

        }
        int c=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(a[i][j]==1)c++;
            }
            cout << endl;
        }

       c%=4;
       if(c==0)return cnt;
       if(c==1)return cnt+1;
       if(f==1)cnt-=2;
       cnt+=c;
        return cnt;


    }





};
