class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int row=grid.size(),col=grid[0].size();

//         if(col==1){
//             int ans=0;
//             if(row%2) ans+=grid[row/2][0];
//             int ones=0,choice=0;
//             for(int i=0;i<row/2;i++){
//                 if(grid[i][0] != grid[row-1-i][0]) choice++;
//                 else ones+=2;
//             }
//             ans+=choice;
//              if((ones % 2 == 2) && !choice) ans+=2;

//             return ans;
//         }

        int ans=0;
        for(int i=0;i<row/2;i++){
            for(int j=0;j<col/2;j++){
                int ones=0;

                if(grid[i][j]) ones++;
                if(grid[i][col-1-j]) ones++;
                if(grid[row-1-i][col-1-j]) ones++;
                if(grid[row-1-i][j]) ones++;

                if(ones==1 || ones==3) ans++;
                else if(ones==2) ans+=2;
            }
        }
        int ones=0,choice=0;
        if(row % 2){
            for(int j=0;j<col/2;j++){
                if(grid[row/2][j] != grid[row/2][col-1-j]) choice++;
                else if(grid[row/2][j]) ones+=2;
            }
        }

        if(col%2){
            for(int i=0;i<row/2;i++){
                if(grid[i][col/2]!=grid[row-1-i][col/2]) 
                choice++;
                else if(grid[i][col/2]) ones+=2;
            }
        }

         ans+=choice;
        // cout<<choice<<' '<<ones;
        if((ones % 4 == 2) && !choice) ans+=2;
        if(row % 2 && col % 2 && grid[row/2][col/2]) ans++;


        return ans;
    }
};
