class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        int n = grid.size(), m = grid[0].size();

        int cnt_m = 0;
        int cnt_m2 = 0;

        if(n&1) {
            for(int j=0; j<=(m-1)/2; j++) {
                if(grid[n/2][j] != grid[n/2][m-1-j]) {
                    cnt_m ++;
                } else if(grid[n/2][j] == 1) {
                    cnt_m2 += 2;
                }
            }
        }

        if(m&1) {
            for(int i=0; i<=(n-1)/2; i++) {
                if(grid[i][m/2] != grid[n-1-i][m/2]) {
                    cnt_m ++;
                } else if(grid[i][m/2] == 1) {
                    cnt_m2 += 2;
                }
            }
        }

        if(cnt_m2 % 4 == 0) {
            res += cnt_m;
        } else {
            if(cnt_m == 0) {
                res += 2;
            } else {
                res += cnt_m;
            }
        }

        if((n&1) && (m&1)) res += grid[n/2][m/2];


        for(int i=0; i<=(n-1)/2; i++) {
            for(int j=0; j<=(m-1)/2; j++) {
                if(((n&1) && (i==n/2)) || ((m&1) && (j==m/2))) {

                } else {
                    int cnt = grid[i][j] + grid[n-1-i][j] + grid[i][m-1-j] + grid[n-1-i][m-1-j];
                    res += min(cnt, 4-cnt);
                }
            }
        }

        return res;
    }
};
