class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();

        int ans=0;

        for(int i=0 ;i<n/2;i++){
            for(int j=0 ;j<m/2;j++){
                int temp=grid[i][j]+grid[n-i-1][j]+grid[i][m-j-1]+grid[n-i-1][m-1-j];
                // cout<<temp<<"\n";
                ans+=min(temp,4-temp);
            }
        }
        // cout<<ans<<"\n";
        if(n&1 && m&1){
            int t1=0;
            int t2=0;
            int sum=0;
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1])t1++;
                else sum+=grid[n/2][j]+grid[n/2][m-j-1];
            }
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2])t2++;
                else sum+=grid[i][m/2]+grid[n-i-1][m/2];
            }

            // cout<<t1<<" "<<t2<<"\n";
            if(t1==0 && t2==0 && sum%4!=0){
                return ans+2+grid[n/2][m/2];
            }

            return ans+t1+t2+grid[n/2][m/2];
        }
        if(n&1){
            int t1=0;
            int sum=0;
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1])t1++;
                else sum+=grid[n/2][j]+grid[n/2][m-j-1];
            }
            if(t1==0 && sum%4!=0)t1+=2;
            return ans+t1;

        }
        if(m&1){
            int t1=0;
            int sum=0;
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2])t1++;
                else sum+=grid[i][m/2]+grid[n-i-1][m/2];
            }
            if(t1==0 && sum%4!=0)t1+=2;
            return ans+t1;

        }
        return ans;
    }
};
