class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int result = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                result += 1;
            }
        }

        vector<int> shiftPlain(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int smal = grid[n / 2][a] + grid[n / 2][b];
                shiftPlain[smal] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int smal = grid[a][m / 2] + grid[b][m / 2];
                shiftPlain[smal] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (shiftPlain[2] % 2 == 1) {
            if (shiftPlain[1] > 0) {
                result += shiftPlain[1];
            } else {
                result += 2;
            }
        } else {
            result += shiftPlain[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int smal = a + b + c + d;

                if (smal != 0 && smal != 4) {
                    result += min(smal, 4 - smal);
                }
            }
        }

        return result;
    }
};
