int minFlips(int** mat, int gs, int* gcs) {
    int n = gs, m = *gcs, res = 0;
    if (n % 2 == 1 && m % 2 == 1) {
        if (mat[n / 2][m / 2] == 1) {
            res += 1;
        }
    }

    int replace[3] = {0, 0, 0};
     if (m % 2 == 1) {
        int a = 0, b = n - 1;
        while (a < b) {
            int sm = mat[a][m / 2] + mat[b][m / 2];
            replace[sm] += 1;
            a += 1;
            b -= 1;
        }
    }
    if (n % 2 == 1) {
        int a = 0, b = m - 1;
        while (a < b) {
            int sm = mat[n / 2][a] + mat[n / 2][b];
            replace[sm] += 1;
            a += 1;
            b -= 1;
        }
    }

    if (replace[2] % 2 == 1) {
        if (replace[1] > 0) {
            res += replace[1];
        } else {
            res += 2;
        }
    } else {
        res += replace[1];
    }
    for (int i = 0; i < n / 2; ++i) {
        for (int j = 0; j < m / 2; ++j) {
            int a = mat[i][j];
            int s = mat[n - 1 - i][j];
            int d = mat[i][m - 1 - j];
            int f = mat[n - 1 - i][m - 1 - j];

            int k = a + s + d + f;

            if (k != 0 && k != 4) {
                res += min(k, 4 - k);
            }
        }
    }

    return res;
}
