class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        c, d = len(grid), len(grid[0])
        e = 0

        if c & 1 and d & 1:
            if grid[c // 2][d // 2] == 1:
                e += 1

        f = [0] * 3
        if c % 2 == 1:
            g, h = 0, d - 1
            while g < h:
                i = grid[c // 2][g] + grid[c // 2][h]
                f[i] += 1
                g += 1
                h -= 1

        if d & 1:
            g, h = 0, c - 1
            while g < h:
                i = grid[g][d // 2] + grid[h][d // 2]
                f[i] += 1
                g += 1
                h -= 1

        if f[2] & 1:
            if f[1] > 0:
                e += f[1]
            else:
                e += 2
        else:
            e += f[1]

        for j in range(c // 2):
            for k in range(d // 2):
                l = grid[j][k]
                m = grid[c - 1 - j][k]
                n = grid[j][d - 1 - k]
                o = grid[c - 1 - j][d - 1 - k]

                i = l + m + n + o

                if i != 0 and i != 4:
                    e += min(i, 4 - i)

        return e
