class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int kik = 0; 
        int hulu = grid.size();
        int lulu = grid[0].size();


        if (hulu % 2 == 1 && lulu % 2 == 1) {
            if (grid[hulu / 2][lulu / 2] != 1) {
                kik;
            }
            else{
                kik += 1;
            }
        }

        vector<int> pik(3, 0);
        if (hulu % 2 == 1) {
            int lik = 0, uuu = lulu - 1;
            while (lik < uuu) {
                int sum = grid[hulu / 2][lik] + grid[hulu / 2][uuu];
                pik[sum]++;
                lik++;
                uuu--;
            }
        }

        if (lulu % 2 == 1) {
            int pluu = 0, sluu = hulu - 1;
            while (pluu < sluu) {
                int sum = grid[pluu][lulu / 2] + grid[sluu][lulu / 2];
                pik[sum]++;
                pluu++;
                sluu--;
            }
        }

        if (pik[2] % 2 == 1) {
            if (pik[1] > 0) {
                kik += pik[1];
            } else {
                kik += 2;
            }
        } else {
            kik += pik[1];
        }

        for (int h = 0; h < hulu / 2; ++h) {
            for (int l = 0; l < lulu / 2; ++l) {
                int bal = grid[hulu - 1 - h][l];
                int bar = grid[hulu - 1 - h][lulu - 1 - l];
                int tal = grid[h][l];
                int tar = grid[h][lulu - 1 - l];

                int tot = tal + bal + tar + bar;

                if (tot != 0 && tot != 4) {
                    kik += min(tot, 4 - tot);
                }
            }
        }

        return kik;
    }
};
