class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size() , m = grid[0].size();
        int res = 0;
        for(int i =0;i<n/2;i++){
            for(int j = 0;j<m/2;j++){
                vector<int> v;
                v.push_back(grid[i][j]);
                v.push_back(grid[n-1-i][j]);
                v.push_back(grid[i][m-1-j]);
                v.push_back(grid[n-1-i][m-1-j]);
                int sm = accumulate(v.begin(),v.end(),0);
                res += min(sm,4-sm);
            }
        }      
        int cg = 0;
        int cnt1 = 0;
        if(n&1){
            int p = n/2;
            for(int x:grid[p]) cnt1 += x;
            for(int j = 0;j<m/2;j++){
                if(grid[p][j] != grid[p][m-1-j]){
                    cg ++;
                }
            }
        }
        if(m&1){
            int p = m/2;
            for(int i =0;i<n;i++) cnt1 += grid[i][p];
            for(int i =0;i<n/2;i++){
                if(grid[i][p] != grid[n-1-i][p]){
                    cg ++;
                }
            }
        }

        if((n&1)&&(m&1)&&grid[n/2][m/2]==1){
            cnt1-=2;res ++;
        }
        res += cg;
        if(cnt1%4==2&&cg == 0) res += 2;
        return res;
    }
};
