class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int flips = 0;

        // Handle the center cell if both dimensions are odd
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                flips += 1;
            }
        }

        // Vector to keep track of necessary flips for middle rows and columns
        vector<int> middleFlips(3, 0);

        // Handle the middle row if rows count is odd
        if (rows % 2 == 1) {
            int left = 0;
            int right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                middleFlips[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        // Handle the middle column if columns count is odd
        if (cols % 2 == 1) {
            int top = 0;
            int bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                middleFlips[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        // Calculate necessary flips based on middle row and column
        if (middleFlips[2] % 2 == 1) {
            if (middleFlips[1] > 0) {
                flips += middleFlips[1];
            } else {
                flips += 2;
            }
        } else {
            flips += middleFlips[1];
        }

        // Process each quadrant to ensure symmetry
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomLeft = grid[rows - 1 - i][j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int totalSum = topLeft + topRight + bottomLeft + bottomRight;

                if (totalSum != 0 && totalSum != 4) {
                    flips += min(totalSum, 4 - totalSum);
                }
            }
        }

        return flips;
    }
};
