class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size();
        int m=g[0].size();
        if(n*m<4){
            int ans=0;
            for(int i=0;i<n;i++){
                for(int j=0;j<m;j++){
                    if(g[i][j]==1)ans++;
                }
            }
            return ans;
        }
        int c=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int a=n-i-1;
                int b=m-j-1;
                int k=0;
                if(i!=a && j!=b){
                    if(g[i][j]==1)k++;
                    if(g[i][b]==1)k++;
                    if(g[a][j]==1)k++;
                    if(g[a][b]==1)k++;
                }
                c+=min(k,4-k);
            }
        }
        if(n%2==0 && m%2==0)return c;
        set<pair<int,int>>s;
        int pc=0;
        int one=0;
        if(m%2==1){
            int c=m/2;
            int i=0,j=n-1;
            while(i<=j){
                if(i==j && g[i][c]==1){
                    s.insert({i,c});
                }
                else if(g[i][c]==1 && g[j][c]==1){
                   s.insert({i,c});
                   s.insert({j,c});
                }
                else if(g[i][c]==0 && g[j][c]==1){
                    pc++;
                }
                else if(g[i][c]==1 && g[j][c]==0){
                    pc++;
                }
                i++,j--;
            }
        }
        if(n%2==1){
            int r=n/2;
            int i=0,j=m-1;
            while(i<=j){
                if(i==j && g[r][i]==1){
                    s.insert({r,i});
                }
                else if(g[r][i]==1 && g[r][j]==1){
                    s.insert({r,i});
                    s.insert({r,j});
                }
               else if(g[r][i]==1 && g[r][j]==0){
                    pc++;
                }
               else if(g[r][i]==0 && g[r][j]==1){
                    pc++;
                }
                i++,j--;
            }
        }
        // cout<<c<<endl;
        one=s.size();
        one=one%4;
        // if(s.size()>0)c++;
        if(one%4==0){
            return c+pc;
        }
        if(pc>=1 && one%2==0)return c+pc;
        if(pc>=1 && one%2==1) return c+pc+1;
        if(one%2==1)return c+pc+one;
         return c+pc+2;
    }
};
