class Solution {
public:
    void helepr(vector<vector<int>>& grid, int n , int m, int & res){
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int cnt = a + b + c + d;

                if (cnt != 0 && cnt != 4) {
                    res += min(cnt, 4 - cnt);
                }
            }
        }
    }

    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int res = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                res += 1;
            }
        }

        vector<int> himank(3, 0);
        if (n % 2 == 1) {
            int i = 0, j = m - 1;
            while (i < j) {
                int cnt = grid[n / 2][i] + grid[n / 2][j];
                himank[cnt] += 1;
                i += 1;
                j -= 1;
            }
        }

        if (m % 2 == 1) {
            int i = 0, j = n - 1;
            while (i < j) {
                int cnt = grid[i][m / 2] + grid[j][m / 2];
                himank[cnt] += 1;
                i += 1;
                j -= 1;
            }
        }

        if (himank[2] % 2 == 1) {
            if (himank[1] > 0) {
                res += himank[1];
            } else {
                res += 2;
            }
        } else {
            res += himank[1];
        }

        helepr(grid, n, m , res);

        return res;

    }
};
