class Solution {
    public int minFlips(int[][] grid) {
        int flip = 0;

        int n = grid.length;
        int m = grid[0].length;

        int set21 = 0, set22 = 0, set41 = 0, set42 = 0, set43 = 0; 

        for( int r=0; r < (n+1)/2; r++ ){
            for( int c=0; c < (m+1)/2; c++ ){

                // single
                if( r == n-r-1 && c == m-c-1 ){
                    if( grid[r][c] == 1 ) flip++;
                }

                // double
                else if( r == n-r-1 ){
                    int count1 = grid[r][c] + grid[r][m-c-1];
                    if( count1 == 1 ) set21++;
                    else if( count1 == 2 ) set22++;
                }

                else if( c == m-c-1 ){
                    int count1 = grid[r][c] + grid[n-r-1][c];
                    if( count1 == 1 ) set21++;
                    else if( count1 == 2 ) set22++;
                }

                // Quad
                else{
                    int count1 = grid[r][c] + grid[r][m-c-1] + grid[n-r-1][c] + grid[n-r-1][m-c-1];
                    if( count1 == 1 ) set41++;
                    else if( count1 == 2 ) set42++;
                    else if( count1 == 3 ) set43++;
                }                
            }


        }

        flip += ( set21 + set41 + set43 + 2*set42 );

        if( set22%2 != 0 ){
            if( set21 == 0 ) flip += 2;
        }

        return flip;
    }
}
