#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int ss(int x, int y, int z, int w) {
        int ones = x + y + z + w;
        return min(ones, 4 - ones);
    }

    int solve(vector<vector<int>>& g, int n, int m) {
        int a = 0;
        int b = 0;
        int c = 0;

        if (n % 2 != 0) {
            for (int j = 0; j < m / 2; ++j) {
                if (g[n / 2][j] != g[n / 2][m - j - 1]) {
                    a += 1;
                    b += 1;
                } else if (g[n / 2][j] == 1) {
                    c += 1;
                }
            }
        }

        if (m % 2 != 0) {
            for (int i = 0; i < n / 2; ++i) {
                if (g[i][m / 2] != g[n - i - 1][m / 2]) {
                    a += 1;
                    b += 1;
                } else if (g[i][m / 2] == 1) {
                    c += 1;
                }
            }
        }

        if (n % 2 != 0 && m % 2 != 0 && g[n / 2][m / 2] == 1) {
            a += 1;
        }

        if (b == 0 && c % 2 != 0) {
            a += 2;
        }

        return a;
    }

    int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();

        int a = 0;

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                a += ss(
                    g[i][j],
                    g[n - i - 1][j],
                    g[i][m - j - 1],
                    g[n - i - 1][m - j - 1]
                );
            }
        }

        a += solve(g, n, m);

        return a;
    }
};
