class Solution {
    //00 20 22 02
    public int minFlips(int[][] g) {
        // int[][] gc = g.clone();
        int m = g.length, n = g[0].length;
        int ans = 0;
        int d1 = m%2==0? m/2:m/2+1, d2 = n%2==0? n/2:n/2+1;
        Map<Integer,Integer> map = new HashMap<>();
        int cnt = 0, k=0;
        for(int i=0; i<d1; i++){
            for(int j=0; j<d2; j++){
                int x = g[i][j], y=g[m-1-i][j], p=g[m-1-i][n-1-j],q=g[i][n-1-j];
                if(n%2==1 && j==n/2 && m%2==1 && i==m/2){
                    if(x==1){cnt++;}
                    continue;
                }
                if(n%2==1 && j==n/2){
                    if(x!=y){ans++;cnt+=2;k++;}
                    if(x==y&&x==1){cnt+=2;}
                    continue;
                }
                if(m%2==1 && i==m/2){
                    if(x!=q){ans++;cnt+=2;k++;}
                    if(x==q&&x==1){cnt+=2;}
                    continue;
                }
                map.clear();
                map.put(x,map.getOrDefault(x,0)+1);
                map.put(y,map.getOrDefault(y,0)+1);
                map.put(p,map.getOrDefault(p,0)+1);
                map.put(q,map.getOrDefault(q,0)+1);
                int max = 0;
                for(int v : map.values()){
                    if(v>max){
                        max=v;
                    }
                }
                ans+=(4-max);
                if(map.containsKey(1)&&map.get(1)>=2){
                    cnt+=4;
                }
            }
        }
        if(cnt%4==0){return ans;}
        if(cnt%4==1){return ans+1;}
        if(cnt%4==2){
            if(k>0){return ans;}
            return ans+=2;
        }
        if(cnt%4==3){
            if(k>0){return ans+1;}
            return ans+3;
        }
        return ans;
    }
}
