class Solution {
public:
    int minFlips(vector<vector<int>>& aa) {
        int n = aa.size();
        int m = aa[0].size();
        int any = 0;
        int one = 0, two = 0;
        int ans = 0;
        int single = 0;
        for (int i = 0; i < (n + 1) / 2; i++) {
            for (int j = 0; j < (m + 1) / 2; j++) {
                set<pair<int, int>> s;
                s.emplace(i, j);
                s.emplace(n - i - 1, j);
                s.emplace(n - i - 1, m - j - 1);
                s.emplace(i, m - j - 1);
                vector<int> res(2);
                for (const auto& [ii, jj] : s) {
                    res[aa[ii][jj]]++;
                }
                if (s.size() == 1) {
                    if (res[1]) {
                        single++;
                    }
                } else if (s.size() == 2) {
                    if (res[0] && res[1]) {
                        any++;
                    } else if (res[0]) {
                        one++;
                    } else {
                        two++;
                    }
                } else {
                    assert(s.size() == 4);
                    ans += ranges::min(res);
                }
            }
        }
        if (any) {
            ans += any;
            if (two & 1) {
                two++;
                any--;
            }
        }
        if (single) {
            ans++;
        }
        if (two & 1) {
            ans += 2;
        }
        return ans;
    }
};
