class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int ans = 0;
        for (int low = 0, high = m - 1; low < high; low++, high--) {
            for (int left = 0, right = n - 1; left < right; left++, right--) {
                int sum = grid[low][left] + grid[low][right] + grid[high][left] + grid[high][right];
                ans += Math.min(sum, 4 - sum);
            }
        }
        if ((m & 1) == 1 && (n & 1) == 1 && grid[m / 2][n / 2] == 1) {
            ans++;
        }
        int r = 0, c = 0, cnt1 = 0;
        if ((m & 1) == 1) {
            int i = m / 2;
            for (int left = 0, right = n - 1; left < right; left++, right--) {
                if (grid[i][left] != grid[i][right]) r++;
                else if (grid[i][left] == 1) cnt1++;
            }
        }
        if ((n & 1) == 1) {
            int j = n / 2;
            for (int low = 0, high = m - 1; low < high; low++, high--) {
                if (grid[low][j] != grid[high][j]) c++;
                else if (grid[low][j] == 1) cnt1++;
            }
        }
        if ((cnt1 & 1) == 1 && r + c == 0) {
            ans += 2;
        } else {
            ans += r + c;
        }
        return ans;
    }
}
