class Solution {
    public int minFlips(int[][] grid) {
        int output = 0,count = 0,yo = 0;
        for(int i=0;i<(grid.length+1)/2;i++){
            for(int j=0;j<(grid[0].length+1)/2;j++){
                int op = grid[i][j] + grid[i][grid[0].length-j-1] + grid[grid.length-1-i][j] + grid[grid.length-1-i][grid[0].length-j-1];
                if((i == grid.length-i-1) && (j == grid[0].length-j-1)) output+=op/4;
                else if((i == grid.length-i-1)) { op /= 2; output += Math.min(op,2-op); if(op == 2) count++; if(op == 1) yo++; }
                else if((j == grid[0].length-j-1)) { op /= 2; output += Math.min(op,2-op); if(op == 2) count++; if(op == 1) yo++; }
                else output += Math.min(op,4-op); 
                //System.out.println(i+"   "+(grid.length-i-1)+"  "+j+"   "+(grid[0].length-j-1));
                //System.out.println(i+"   "+j+"   "+op+"   "+output+"   "+grid[0].length/2+"    "+grid.length/2);

            }
        }
        if(count %2 != 0 && yo == 0) output += 2;
        return output;
    }
}
