class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
      int n = grid.size();
      int  m = grid[0].size();
        int res = 0;

if ( m % 2 == 1&&n % 2 == 1 ) {
            if (grid[n / 2][m / 2] == 1) {
                res += 1;
            }
        }

        vector<int> shift(3, 0);
        if (n % 2 == 1) {
            int ab = 0, ba = m - 1;
            while (ab < ba) {
                int sm = grid[n / 2][ab] + grid[n / 2][ba];
                shift[sm] += 1;
                ab+= 1;
                ba -= 1;
            }
        }

        if (m % 2 == 1) {
            int ab = 0, ba = n - 1;
            while (ab < ba) {
                int sm = grid[ab][m / 2] + grid[ba][m / 2];
                shift[sm] += 1;
                ab += 1;
                ba -= 1;
            }
        }

        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                res+= shift[1];
            } else {
                res+= 2;
            }
        } else {
            res += shift[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sm = a + b + c + d;

                if (sm != 0 && sm != 4) {
                    res += min(sm, 4 - sm);
                }
            }
        }

        return res;   
    }
};
