class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int rs = 0, re = m-1, cs = 0, ce = n-1, ans = 0;

        while(rs < re)
        {
            cs = 0;
            ce = n-1;

            while(cs < ce)
            {
                int c1 = 0, c0 = 0;

                if(grid[rs][cs] == 1) c1++;
                else c0++;

                if(grid[rs][ce] == 1) c1++;
                else c0++;

                if(grid[re][cs] == 1) c1++;
                else c0++;

                if(grid[re][ce] == 1) c1++;
                else c0++;

                ans += min(c1, c0);

                // cout << c0 << " " << c1 << endl;

                cs++;
                ce--;
            }

            rs++;
            re--;
        }

        if(m%2 == 1 && n%2 == 1)
        {
            if(grid[m/2][n/2] == 1) ans++;

            int c = 0, changed = 0;
            int s = 0, e = n-1;

            while(s < e)
            {
                if(grid[m/2][s] == grid[m/2][e] && grid[m/2][s] == 1) c += 2;
                else if(grid[m/2][s] != grid[m/2][e]) changed++;
                s++;
                e--;
            }

            s = 0; e = m-1;

            while(s < e)
            {
                if(grid[s][n/2] == grid[e][n/2] && grid[s][n/2] == 1) c += 2;
                else if(grid[s][n/2] != grid[e][n/2]) changed++;
                s++;
                e--;
            }

            if(c%4 != 0)
            {
                if(changed >= 1) ans += changed;
                else ans += 2;
            }
            else ans += changed;
        }

        else if(m%2 == 1)
        {
            int c = 0, changed = 0;
            int s = 0, e = n-1;

            while(s < e)
            {
                if(grid[m/2][s] == grid[m/2][e] && grid[m/2][s] == 1) c += 2;
                else if(grid[m/2][s] != grid[m/2][e]) changed++;
                s++;
                e--;
            }

            if(c%4 != 0)
            {
                if(changed >= 1) ans += changed;
                else ans += 2;
            }
            else ans += changed;
        }

        else if(n%2 == 1)
        {
            int c = 0, changed = 0;
            int s = 0, e = m-1;

            while(s < e)
            {
                if(grid[s][n/2] == grid[e][n/2] && grid[s][n/2] == 1) c += 2;
                else if(grid[s][n/2] != grid[e][n/2]) changed++;
                s++;
                e--;
            }

            if(c%4 != 0)
            {
                if(changed >= 1) ans += changed;
                else ans += 2;
            }
            else ans += changed;
        }

        return ans;
    }
};
