class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        ans = 0
        for i in range(n // 2):
            for j in range(m // 2):
                cnt = 0
                cnt += grid[i][j]
                cnt += grid[-i-1][j]
                cnt += grid[i][-j-1]
                cnt += grid[-i-1][-j-1]
                ans += min(cnt, 4 - cnt)

        cnt = 0
        f = False
        if n % 2:
            for j in range(m // 2):
                if grid[n // 2][j] != grid[n // 2][-j-1]:
                    ans += 1
                    f = True
                cnt += grid[n // 2][j] + grid[n // 2][-j-1]
        if m % 2:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[-i-1][m // 2]:
                    ans += 1
                    f = True
                cnt += grid[i][m // 2] + grid[-i-1][m // 2]
        if n % 2 and m % 2:
            ans += grid[n // 2][m // 2]
        if not f and cnt % 4 == 2:
            ans += 2
        return ans
