class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int ans=0,res=0;
        int n=a.size(),m=a[0].size();
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int t=a[i][m-j-1];
                int d=a[n-i-1][j];
                int u=a[n-i-1][m-j-1];
                int cnt=a[i][j]+t+d+u;
                res+=std::min(cnt,4-cnt);
            }
        }
        int cnt=0,k=0;
        if(n&1){
            int h=n/2;
            for(int l=0,r=m-1;l<r;l++,r--){
                if(a[h][l]==a[h][r]){
                    if(a[h][l]==1) cnt+=2;
                }else k++;
            }
        }
        if(m&1){
            int h=m/2;
            for(int l=0,r=n-1;l<r;l++,r--){
                if(a[l][h]==a[r][h]){
                    if(a[l][h]==1) cnt+=2;
                }else k++;
            }
        }
        if(n&1 and m&1 and a[n/2][m/2]==1) res++;
        if(cnt%4){
            if(!k) res+=2;
            else res+=k;
        }else res+=k;
        return res;
    }
};
