class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = 0;
        for (int i = 0; i < n/2; i++) {
            for (int j = 0; j < m/2; j++) {
                int c0 = 0, c1 = 0;
                c0 += grid[i][j] == 0;
                c1 += grid[i][j] == 1;
                c0 += grid[i][m-1-j] == 0;
                c1 += grid[i][m-1-j] == 1;
                c0 += grid[n-1-i][j] == 0;
                c1 += grid[n-1-i][j] == 1;
                c0 += grid[n-1-i][m-1-j] == 0;
                c1 += grid[n-1-i][m-1-j] == 1;
                ans += min(c0, c1);
            }
        }
        int c1 = 0, mcnt = 0;
        if (n%2) {
            for (int j = 0; j < m/2; j++) {
                if (grid[n/2][j] != grid[n/2][m-1-j]) {
                    ans++;
                    mcnt++;
                    c1 = (c1 + 2) % 4;
                } else if (grid[n/2][j] == 1) {
                    c1 = (c1 + 2) % 4;
                }
            }
        }
        if (m%2) {
            for (int i = 0; i < n/2; i++) {
                if (grid[i][m/2] != grid[n-1-i][m/2]) {
                    ans++;
                    mcnt++;
                    c1 = (c1 + 2) % 4;
                } else if (grid[i][m/2] == 1) {
                    c1 = (c1 + 2) % 4;
                }
            }
        }
        if (m%2 && n%2 && grid[n/2][m/2] == 1) { ans++; }
        if (c1 == 2 && mcnt == 0) { ans += 2; }
        return ans;
    }
};
